import Snake from "./Snake";
import Point from "./Point";
import IWorldView from "./IWorldView"

class WorldModel {
  private snake: Snake;
  private width: number;
  private height: number;
  private worldView: IWorldView | null = null;

  constructor(s: Snake, width: number, height: number) {
    this.snake = s;
    this.width = width;
    this.height = height;
    this.worldView = null;
  }

  update(steps: number): void {
    this.snake.move(steps);
    if (this.worldView !== null) {
            this.worldView.display(this);
        }
  }

  worldWidth(): number {
    return this.width;
  }

  worldHeight(): number {
    return this.height;
  }

  get Snake(): Snake {
    return this.snake;
  }

  set Snake(s:Snake) {
    this.Snake = s;
}

  get WorldView(): IWorldView | null {
    return this.worldView;
  }

  set WorldView(view: IWorldView | null) {
    this.worldView = view;
  }

  setView(view: IWorldView): void {
    this.worldView = view;
  }
}

export default WorldModel;
